import React, { useState } from 'react';
import './App.css';

// Компонент хедера
const Header = () => {
  return (
    <div className="header">
      <h1>Мій перший React додаток</h1>
    </div>
  );
};

// Компонент лівої бічної навігації
const Sidebar = () => {
  return (
    <div className="sidebar">
      <ul>
        <li>Пункт 1</li>
        <li>Пункт 2</li>
        <li>Пункт 3</li>
      </ul>
    </div>
  );
};

// Компонент центрального контейнера
const MainContainer = () => {
  return (
    <div className="main-container">
      <p>Вміст центрального контейнера</p>
    </div>
  );
};

// Основний компонент додатку
const App = () => {
  return (
    <div className="app">
      <Header />
      <div className="content">
        <Sidebar />
        <MainContainer />
      </div>
    </div>
  );
};

export default App;
